import React, { useState, useEffect } from "react";
import axios from "axios";
import Createposts from "./Createposts";
import "./Community.css";
// import Comment from "./Comment";
import CommentForm from "./Comment";
import SaveImage from "./profilepics";
function Community() {
  const [image, setImage] = useState(null);
  const [showIcon, setShowIcon] = useState(true);
  const [posts, setPosts] = useState([]);
  const [comments, setComments] = useState([]);
  const [error, setError] = useState("");
  const [showCommentForm, setShowCommentForm] = useState(null);
  const [userid, setUserId] = useState(null);
  const [content, setContent] = useState("");

  useEffect(() => {
    const userData = JSON.parse(localStorage.getItem("data")) || [];
    userData.forEach((user) => {
      setUserId(parseInt(user.id));
    });
  }, []);
  const handleImageChange = (event) => {
    if (event.target.files && event.target.files[0]) {
      const formData = new FormData();
      formData.append("profile_pic", event.target.files[0]);
      axios
        .post("http://127.0.0.1:8000/api/save_image/", formData, {
          headers: {
            "Content-Type": "multipart/form-data",
            Authorization: `Bearer ${localStorage.getItem("access_token")}`,
          },
        })
        .then((response) => {
          const newImage = URL.createObjectURL(event.target.files[0]);
          localStorage.setItem("userImage", newImage);
          setImage(newImage);
          setShowIcon(false);
          console.log(response.data);
        })
        .catch((error) => {
          console.log(error);
        });
    }
  };
  const handleSubmitComment = async (event, postId, userId, setContent) => {
    event.preventDefault();
    const url = `http://127.0.0.1:8000/api/users/${userId}/posts/${postId}/comments/create/`;
    const data = { content };
    const headers = {
      "Content-Type": "application/json",
      Authorization: `Bearer ${localStorage.getItem("access_token")}`,
    };
    try {
      const response = await axios.post(url, data, { headers });
      console.log(response.data);
      setComments((prevComments) => [...prevComments, response.data]);
      setContent(""); // clear the content input
    } catch (error) {
      console.error(error.response);
      setError(error.response.data.error);
    }
  };
  const handleIconClick = () => {
    const fileInput = document.getElementById("image-upload");
    fileInput.click();
  };

  const handlePostCreated = (post) => {
    setPosts([...posts, post]);
  };
  // const handleCommentCreated = (postId, comment) => {
  //   const updatedPosts = posts.map((post) => {
  //     if (post.id === postId) {
  //       return {
  //         ...post,
  //         comments: [...post.comments, comment],
  //       };
  //     } else {
  //       return post;
  //     }
  //   });
  //   setPosts(updatedPosts);
  // };
  const handleCommentCreated = (comment) => {
    setComments([...comments, comment]);
  };
  useEffect(() => {
    const userData = JSON.parse(localStorage.getItem("data")) || [];
    userData.forEach((user) => {
      setUserid(user.id);
    });
    const fetchData = async () => {
      try {
        const response = await axios.get("http://127.0.0.1:8000/api/posts/");
        const promises = response.data.map((post) =>
          axios.get(`http://127.0.0.1:8000/api/posts/${post.id}/comments/`)
        );
        const commentResponses = await Promise.all(promises);
        const postsWithComments = response.data.map((post, index) => ({
          ...post,
          comments: commentResponses[index].data,
        }));
        setPosts(postsWithComments);
      } catch (error) {
        console.error(error);
      }
    };
    fetchData();
  }, []);

  return (
    <>
      <img
        src="http://127.0.0.1:8000/profile_pics/PC-Desktop-Backgrounds.jpg"
        alt="Profile"
      />
      {error && <div className="error">{error}</div>}
      <section className="POSTS-BOX">
        <div className="Profile">
          <div className="bg-pro">
            <div className="img-pro">
              <SaveImage user={userid} />
            </div>
          </div>

          <div className="Info">
            <div className="text-name">
              <p>John Doe</p>
              <p>Web Developer at Self Employed</p>
            </div>
            <div className="following">
              <span>Following</span>
              <span>34</span>
            </div>
            <div className="followers">
              <span>Follower</span>
              <span>255</span>
            </div>
          </div>
          <button>View Profile</button>
        </div>
        <div className="POSTS">
          <Createposts onPostCreated={handlePostCreated} image={image} />

          {posts.map((post) => (
            <div key={post.id} className="box-pos">
              <ul className="com">
                <li>
                  <button
                    onClick={() => setShowCommentForm(post.id)}
                    className="com"
                  >
                    <i className="fas fa-comment-alt comment"></i> Add Comment
                  </button>
                </li>
              </ul>
              <h2>{post.title}</h2>
              <p>{post.content}</p>
              {post.comments?.map((comment) => (
                <div key={comment.id}>
                  {comment.user && (
                    <h5> Commenter: {comment.user?.username}</h5>
                  )}
                  <li>{comment.content}</li>
                </div>
              ))}
              {showCommentForm === post.id && (
                <CommentForm
                  postId={post.id}
                  userId={userid}
                  handleSubmitComment={handleCommentCreated}
                  setContent={setContent}
                  setError={setError}
                />
              )}
              {comments.map((comment) => (
                <div key={comment.id}>
                  <p>{comment.content}</p>
                </div>
              ))}
            </div>
          ))}
        </div>
      </section>
    </>
  );
}

export default Community;
