import React, { useState, useEffect } from "react";
import "./Nav_bar.css";
import Container from "react-bootstrap/Container";
import Nav from "react-bootstrap/Nav";
import Navbar from "react-bootstrap/Navbar";
import img1 from "../image/icon.png";
import { useNavigate, Link, NavLink, redirect } from "react-router-dom";
import Movies from "./Movies";

function Nav_bar() {
  const navigate = useNavigate();
  const [username, setUserLogin] = useState("");
  const [navbar, setNavbar] = useState(false);
  const logout = () => {
    localStorage.clear("user");
    navigate("/Login");
  };
  useEffect(() => {
    const userData = JSON.parse(localStorage.getItem("user")) || [];
    userData.forEach((user) => {
      setUserLogin(user.name);
    });
  });
  return (
    <>
      <Navbar className="Navbar" expand="lg">
        <Container fluid>
          <Navbar.Brand href="#" className="logo">
            <img src={img1} alt="" />
          </Navbar.Brand>
          <Navbar.Toggle aria-controls="navbarScroll" />
          <Navbar.Collapse id="navbarScroll">
            <Nav
              className="ms-auto my-2 my-lg-0 links"
              style={{ maxHeight: "100px" }}
              navbarScroll
            >
              <Nav.Link
                className="navlink"
                as={NavLink}
                to="/"
                activeClassName="active-link"
              >
                Home
              </Nav.Link>
              <Nav.Link className="navlink" href="#about">
                About
              </Nav.Link>
              <Nav.Link className="navlink" href="#categories">
                Categories
              </Nav.Link>
              {username && (
                <Nav.Link className="navlink" as={NavLink} to="/home/movies">
                  Movies
                </Nav.Link>
              )}
            </Nav>
            <Nav className="ms-auto links">
              {username ? (
                <>
                  <Nav.Link className="navlink" disabled>
                    Hi {username}
                  </Nav.Link>
                  <Nav.Link className="navlink" onClick={logout}>
                    Logout
                  </Nav.Link>
                </>
              ) : (
                <Nav.Link
                  className="navlink"
                  as={NavLink}
                  to="/Login"
                  activeClassName="active-link"
                >
                  Login
                </Nav.Link>
              )}
            </Nav>
          </Navbar.Collapse>
        </Container>
      </Navbar>
    </>
  );
}

export default Nav_bar;
