import React, { useState, useEffect } from "react";
import axios from "axios";
import Createposts from "./Createposts";
import img_pro from "../image/icon.png";
import "./Community.css";
function Community() {
  const [posts, setPosts] = useState([]);
  const [error, setError] = useState(null);

  const handlePostCreated = (post) => {
    setPosts([...posts, post]);
  };

  const handleDeletePost = async (postId) => {
    const url = `http://127.0.0.1:8000/api/posts/${postId}/delete/`;
    const headers = {
      Authorization: `Bearer ${localStorage.getItem("access_token")}`,
    };

    try {
      const response = await axios.delete(url, { headers });
      console.log(response.data);
      // Update the posts state to remove the deleted post
      setPosts(posts.filter((post) => post.id !== postId));
    } catch (error) {
      console.error(error);
      setError(error.response.data.error);
    }
  };

  useEffect(() => {
    axios
      .get("http://127.0.0.1:8000/api/posts/")
      .then((response) => {
        setPosts(response.data);
        localStorage.setItem("user_id", response.data.user_id);
      })
      .catch((error) => console.error(error));
  }, []);

  return (
    <>
      {error && <div className="error">{error}</div>}
      {/* <Createposts onPostCreated={handlePostCreated} /> */}
      <section className="POSTS-BOX">
        <div className="Profile">
          <div className="bg-pro">
            <div className="img-pro">
              <img src={img_pro} alt="" />
            </div>
          </div>

          <div className="Info">
            <div className="text-name">
              <p>John Doe</p>
              <p>Web Developer at Self Employed</p>
            </div>
            <div className="following">Following 34</div>
            <div className="followers">Followers 54 </div>
          </div>
        </div>
        {posts.map((post) => (
          <div key={post.id} className="POSTS">
            <h2>{post.title}</h2>
            <p>{post.content}</p>
          </div>
        ))}
      </section>
    </>
  );
}

export default Community;
