import React, { useState, useEffect } from "react";
import axios from "axios";
import Createposts from "./Createposts";
import img_pro from "../image/icon.png";
import "./Community.css";
import Comment from "./Comment";
function Community() {
  const [posts, setPosts] = useState([]);
  const [error, setError] = useState(null);
  const [comment, setComment] = useState([]);
  const handlePostCreated = (post) => {
    setPosts([...posts, post]);
  };
  const handleAddComment = (commentText) => {
    const url = "http://127.0.0.1:8000/api/comments/create/1";
    const headers = {
      Authorization: `Bearer ${localStorage.getItem("access_token")}`,
    };
    const data = { text: commentText };

    try {
      const response = axios.post(url, data, { headers });
      console.log(response.data);
      // Update the comment state with the new comment
      setComment([...comment, response.data]);
    } catch (error) {
      console.error(error);
      setError(error.response.data.error);
    }
  };
  useEffect(() => {
    axios
      .get("http://127.0.0.1:8000/api/posts/")
      .then((response) => {
        setPosts(response.data);
        localStorage.setItem("user_id", response.data.user_id);
      })
      .catch((error) => console.error(error));
  }, []);

  return (
    <>
      {error && <div className="error">{error}</div>}
      <section className="POSTS-BOX">
        <div className="Profile">
          <div className="bg-pro">
            <div className="img-pro">
              <img src={img_pro} alt="" />
            </div>
          </div>

          <div className="Info">
            <div className="text-name">
              <p>John Doe</p>
              <p>Web Developer at Self Employed</p>
            </div>
            <div className="following">
              <span>Following</span>
              <span>34</span>
            </div>
            <div className="followers">
              <span>Follower</span>
              <span>255</span>
            </div>
          </div>
          <button>View Profile</button>
        </div>
        <div className="POSTS">
          <Createposts onPostCreated={handlePostCreated} />

          {posts.map((post) => (
            <div key={post.id} className="box-pos">
              <h2>{post.title}</h2>
              <p>{post.content}</p>
            </div>
          ))}
        </div>
      </section>
    </>
  );
}

export default Community;
