import React from "react";
import "./Login.css";
import "bootstrap/dist/css/bootstrap.min.css";
import "jquery/dist/jquery.min.js";
import "bootstrap/dist/js/bootstrap.min.js";
import img1 from "../image/icon.png";
import swal from "sweetalert";
import { Link, useNavigate } from "react-router-dom";
import { useState } from "react";
function Login({ OnuserCreated }) {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const navigate = useNavigate();

  const handleLogin = (e) => {
    e.preventDefault();
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [error, setError] = useState("");
    axios
      .post("/login/", { email, password })
      .then((response) => {
        window.location.href = "/home/";
      })
      .catch((error) => {
        setError("Invalid email or password.");
      });
  };
  //   if (validationName() && validationEmail() && validationPassword()) {
  //     navigate("/");
  //     setError("");
  //   }
  // };
  // const validationName = () => {
  //   const regx = /^\w{3,8}$/;
  //   if (regx.test(name)) {
  //     return true;
  //   } else {
  //     setError("Name Invalid...!");
  //     return false;
  //   }
  // };

  // const validationEmail = () => {
  //   const regx = /^[\w-.]+@([\w-]+.)+[\w-]{2,4}$/;
  //   if (regx.test(email)) {
  //     return true;
  //   } else {
  //     setError("Email Invalid...!");
  //     return false;
  //   }
  // };

  // const validationPassword = () => {
  //   const regx = /^(?=.*\d)(?=.*[a-z])(?=.*[A-Z])(?=.*[a-zA-Z]).{8,}$/;
  //   if (regx.test(password)) {
  //     return true;
  //   } else {
  //     setError("Password Invalid...!");
  //     return false;
  //   }
  // };

  const handleAlertClick = () => {
    swal({
      title: "مــــعــــلــــــش هه",
      timer: 4000,
    });
  };
  return (
    <>
      <div className="contain">
        <div className="containerr">
          {error && <div className="alert alert-danger">{error}</div>}
          <img src={img1} alt="" />
          <form action="" method="post" onSubmit={handleLogin}>
            <div className="inputs">
              <input
                type="text"
                placeholder="Enter your name..."
                id="name"
                value={name}
                name="name"
                onChange={(e) => setName(e.target.value)}
              />
              <input
                type="text"
                placeholder="Enter your email..."
                name="email"
                className="form-control"
                id="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
              />
              <input
                type="password"
                placeholder="Enter your password..."
                name="password"
                className="form-control"
                id="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
            </div>
            <div className="login">
              <button type="submit" onClick={handleLogin} className="login">
                Login
              </button>
              <Link href="#" onClick={handleAlertClick}>
                Forget Password ?
              </Link>
            </div>
          </form>
        </div>
      </div>
    </>
  );
}

export default Login;
