import React, { useState, useEffect } from "react";
import "./Nav_bar.css";
import Container from "react-bootstrap/Container";
import Nav from "react-bootstrap/Nav";
import Navbar from "react-bootstrap/Navbar";
import img1 from "../image/icon.png";
import { useNavigate, Link, NavLink } from "react-router-dom";
import Movies from "./Movies";

function Nav_bar(props) {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [username, setUsername] = useState("");
  const navigate = useNavigate();
  const { user, setUser } = props;
  useEffect(() => {
    const storedUsername = localStorage.getItem("name");
    if (storedUsername) {
      setUsername(storedUsername);
      setIsLoggedIn(true);
    }
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("name");
    setUsername("");
    setIsLoggedIn(false);
    navigate("/", { replace: true });
  };

  const handleLogin = (event) => {
    event.preventDefault();
    const username = event.target.name.value;
    localStorage.setItem("name", username);
    setUsername(username);
    setIsLoggedIn(true);
    navigate("/", { replace: true });
  };

  const navbarClass = isLoggedIn ? "Navbar logged-in" : "Navbar";

  return (
    <>
      <Navbar className={navbarClass} expand="lg">
        <Container fluid>
          <Navbar.Brand href="#" className="logo">
            <img src={img1} alt="" />
          </Navbar.Brand>
          <Navbar.Toggle aria-controls="navbarScroll" />
          <Navbar.Collapse id="navbarScroll">
            <Nav
              className="ms-auto my-2 my-lg-0 links"
              style={{ maxHeight: "100px" }}
              navbarScroll
            >
              <Nav.Link
                className="navlink"
                as={NavLink}
                to="/"
                activeClassName="active-link"
              >
                Home
              </Nav.Link>
              <Nav.Link className="navlink" href="#about">
                About
              </Nav.Link>
              <Nav.Link className="navlink" href="#categories">
                Categories
              </Nav.Link>
              {isLoggedIn && (
                <Nav.Link className="navlink" as={NavLink} to="/movies">
                  Movies
                </Nav.Link>
              )}
            </Nav>
            <Nav className="ms-auto links">
              {isLoggedIn ? (
                <>
                  <Nav.Link className="navlink" disabled>
                    Hi, {username}
                  </Nav.Link>
                  <Nav.Link className="navlink" onClick={handleLogout}>
                    Logout
                  </Nav.Link>
                </>
              ) : (
                <Nav.Link
                  className="navlink"
                  as={NavLink}
                  to="/Login"
                  activeClassName="active-link"
                >
                  Login
                </Nav.Link>
              )}
            </Nav>
          </Navbar.Collapse>
        </Container>
      </Navbar>
    </>
  );
}

export default Nav_bar;
