import React, { useEffect, useState } from "react";
import axios from "axios";
import { FaPlus } from "react-icons/fa";
import "./profilepics.css";
const SaveImage = ({ user }) => {
  const [profilePic, setProfilePic] = useState(null);
  const [error, setError] = useState(null);
  const [userid, setUserid] = useState("");
  const [imageUrl, setImageUrl] = useState(null);

  useEffect(() => {
    const userData = JSON.parse(localStorage.getItem("data")) || [];
    userData.forEach((user) => {
      setUserid(user.id);
    });
  });

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append("profile_pic", profilePic);
    try {
      const response = await axios.post(
        `http://127.0.0.1:8000/api/save-image/${userid}/`,
        formData,
        {
          headers: {
            "Content-Type": "multipart/form-data",
          },
        }
      );
      console.log(response.data);
      setImageUrl(response.data.profile_pic_url);
    } catch (err) {
      console.error(err);
      setError(err.response.data.error);
    }
  };

  const handleFileChange = (e) => {
    setProfilePic(e.target.files[0]);
  };

  return (
    <div>
      {error && <div>{error}</div>}
      <form onSubmit={handleSubmit}>
        <div className="relative w-32 h-32 overflow-hidden rounded-full">
          {imageUrl ? (
            <img
              src={imageUrl}
              alt="Profile Picture"
              className="absolute top-0 left-0 w-full h-full object-cover"
            />
          ) : (
            <div className="absolute top-0 left-0 w-full h-full flex items-center justify-center text-gray-400 bg-gray-100">
              <label htmlFor="profile-pic-input">
                <FaPlus size={20} />
              </label>
              <input
                id="profile-pic-input"
                type="file"
                className="hidden"
                onChange={handleFileChange}
              />
            </div>
          )}
        </div>
        <button type="submit">Save Image</button>
      </form>
    </div>
  );
};

export default SaveImage;
