import './App.css';
import Login from './components/Login';
import { useState } from 'react';
import { Routes, Route ,useLocation } from 'react-router-dom';
import Home from './components/Home';
import Movies from './components/Movies';
import Nav_bar from './components/Navbar';
import Footer from './components/Footer';

function App() {
  const location = useLocation();
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [user, setUser] = useState(JSON.parse(localStorage.getItem("user")) || {});

  // const handleLogin = (user) => {
  //   // setUser(user);
  //   // setIsLoggedIn(true);
  //   localStorage.setItem("user", JSON.stringify(user));
  // };

  const handleLogout = () => {
    setUser({});
    setIsLoggedIn(false);
    localStorage.removeItem("user");
  };

  const athome = location.pathname.includes('/home')
  const atmovie = location.pathname.includes('/home/movies')

  return (
    <>
      {athome && atmovie && <Nav_bar isLoggedIn={isLoggedIn} user={user} handleLogout={handleLogout}/>}
      <div className="overlay"></div>
      <Routes>
        <Route path='/' element={<Home />}/>
        <Route path='/Login' element={<Login />} />
        {isLoggedIn && <Route path='/home/movies'element={<Movies/>} />}
      </Routes>
      {athome &&atmovie && <Footer/>}
    </>
  );
}

export default App;
