import React, { useState, useEffect } from "react";
import axios from "axios";
import Createposts from "./Createposts";
import "./Community.css";
// import Comment from "./Comment";
import CommentForm from "./Comment";
import SaveImage from "./profilepics";
function Community() {
  const [image, setImage] = useState(null);
  const [showIcon, setShowIcon] = useState(true);
  const [posts, setPosts] = useState([]);
  const [comments, setComments] = useState([]);
  const [error, setError] = useState("");
  const [showCommentForm, setShowCommentForm] = useState(null);
  const [userid, setUserId] = useState(null);
  const [content, setContent] = useState("");
  const handleDelete = async (postId) => {
    try {
      await axios.delete(
        "http://127.0.0.1:8000/api/user/${userid}/post/${postId}/delete/"
      );
      console.log("Post deleted successfully");
      window.location.reload(true);
    } catch (error) {
      console.error(error);
    }
  };

  const handlePostCreated = (post) => {
    setPosts([...posts, post]);
  };
  const handleCommentCreated = (postId, comment) => {
    const updatedPosts = posts.map((post) => {
      if (post.id === postId) {
        return {
          ...post,
          comments: [...post.comments, comment],
        };
      } else {
        return post;
      }
    });
    setPosts(updatedPosts);
  };
  // const handleCommentCreated = (comment) => {
  //   setComments([...comments, comment]);
  // };
  useEffect(() => {
    const userData = JSON.parse(localStorage.getItem("data")) || [];
    userData.forEach((user) => {
      setUserId(user.id);
    });
    const fetchData = async () => {
      try {
        const response = await axios.get("http://127.0.0.1:8000/api/posts/");
        const promises = response.data.map((post) =>
          axios.get(`http://127.0.0.1:8000/api/posts/${post.id}/comments/`)
        );
        const commentResponses = await Promise.all(promises);
        const postsWithComments = response.data.map((post, index) => ({
          ...post,
          comments: commentResponses[index].data,
        }));
        setPosts(postsWithComments);
      } catch (error) {
        if (
          error.response.status === 400 &&
          error.response.data.non_field_errors
        ) {
          setError(error.response.data.non_field_errors[0]);
        } else {
          setError(error.message);
          console.log(error);
        }
      }
    };
    fetchData();
  }, []);

  return (
    <>
      <img
        src="http://127.0.0.1:8000/profile_pics/PC-Desktop-Backgrounds.jpg"
        alt="Profile"
      />
      {error && <div className="error">{error}</div>}
      <section className="POSTS-BOX">
        <div className="Profile">
          <div className="bg-pro">
            <div className="img-pro">
              <SaveImage user={userid} />
            </div>
          </div>

          <div className="Info">
            <div className="text-name">
              <p>John Doe</p>
              <p>Web Developer at Self Employed</p>
            </div>
            <div className="following">
              <span>Following</span>
              <span>34</span>
            </div>
            <div className="followers">
              <span>Follower</span>
              <span>255</span>
            </div>
          </div>
          <button>View Profile</button>
        </div>
        <div className="POSTS">
          <Createposts onPostCreated={handlePostCreated} image={image} />

          {posts.map((post) => (
            <div key={post.id} className="box-pos">
              <ul className="com">
                <li>
                  <button
                    onClick={() => setShowCommentForm(post.id)}
                    className="com"
                  >
                    <i className="fas fa-comment-alt comment"></i> Add Comment
                  </button>
                </li>
              </ul>
              <h2>{post.title}</h2>
              <p>{post.content}</p>
              {post.comments?.map((comment) => (
                <div key={comment.id}>
                  {comment.user && <h5> Commenter: {comment.user?.name}</h5>}
                  <li>{comment.content}</li>
                </div>
              ))}
              {post.user.id === userid && (
                <div className="deleting">
                  <button
                    className="delete-buttom"
                    onClick={() => handleDelete(post.id)}
                  >
                    Delete
                  </button>
                </div>
              )}
              {showCommentForm === post.id && (
                <CommentForm
                  postId={post.id}
                  onSubmit={(comment) => handleCommentCreated(post.id, comment)}
                />
              )}
              {comments.map((comment) => (
                <div key={comment.id}>
                  <p>{comment.content}</p>
                </div>
              ))}
            </div>
          ))}
        </div>
      </section>
    </>
  );
}

export default Community;
