import React, { useState, useEffect } from "react";
import "./Nav_bar.css";
import Container from "react-bootstrap/Container";
import Nav from "react-bootstrap/Nav";
import Navbar from "react-bootstrap/Navbar";
import img1 from "../image/icon.png";
import { useNavigate, Link, NavLink } from "react-router-dom";
import Movies from "./Movies";
function Nav_bar(history) {
  const [navbar, setnavbar] = useState(false);
  const [userLogin, setUserLogin] = useState(false);
  const Navigate = useNavigate();
  const redirect = () => {
    Navigate("/home/movies");
  };
  const Redirecttologin = () => {
    Navigate("/home/Login");
  };
  const redirecttohome = () => {
    Navigate("/");
  };
  const handleogout = () => {
    localStorage.clear("data");
    Navigate("/", "_self");
  };
  useEffect(() => {
    const userData = JSON.parse(localStorage.getItem("data")) || [];
    userData.forEach((user) => {
      setUserLogin(user.name);
    });
  });
  const changewidth_navbar = () => {
    if (window.scrollY >= 100) {
      setnavbar(true);
    } else if (window.scrollY < 100) {
      setnavbar(false);
    }
  };
  window.addEventListener("scroll", changewidth_navbar);
  return (
    <>
      <Navbar className={navbar ? "Navbar active" : "Navbar"} expand="lg">
        <Container fluid>
          <Navbar.Brand href="#" className="logo">
            <img src={img1} alt="" />
          </Navbar.Brand>
          <Navbar.Toggle aria-controls="navbarScroll" />
          <Navbar.Collapse id="navbarScroll">
            <Nav
              className="ms-auto my-2 my-lg-0 links"
              style={{ maxHeight: "100px" }}
              navbarScroll
            >
              <Nav.Link
                className="navlink"
                href=""
                onClick={redirecttohome}
                activeClassName="active-link"
              >
                <Link className="link">Home</Link>
              </Nav.Link>
              <Nav.Link className="navlink" href="#about">
                About
              </Nav.Link>
              <Nav.Link className="navlink" href="#categories">
                Categoires
              </Nav.Link>
              <Nav.Link className="navlink" onClick={redirect}>
                <Link className="link">Movies</Link>
              </Nav.Link>
            </Nav>
            <Nav className="ms-auto links">
              {userLogin ? (
                <>
                  <Nav.Link className="navlink" id="login" href="#home">
                    Hi {userLogin}
                  </Nav.Link>
                  <Nav.Link
                    className="navlink"
                    id="login"
                    href="#home"
                    onClick={handleogout}
                  >
                    Log out
                  </Nav.Link>
                </>
              ) : (
                <Nav.Link
                  className="navlink"
                  onClick={Redirecttologin}
                  id="login"
                  href="#home"
                >
                  <Link className="link">Login</Link>
                </Nav.Link>
              )}
            </Nav>
          </Navbar.Collapse>
        </Container>
      </Navbar>
    </>
  );
}

export default Nav_bar;
