import React, { useState, useEffect } from "react";
import axios from "axios";
import Createposts from "./Createposts";
import img_pro from "../image/icon.png";
import "./Community.css";
// import Comment from "./Comment";
import CommentForm from "./Comment";
function Community() {
  const [image, setImage] = useState(null);
  const [showIcon, setShowIcon] = useState(true);

  const handleImageChange = (event) => {
    if (event.target.files && event.target.files[0]) {
      const newImage = URL.createObjectURL(event.target.files[0]);
      localStorage.setItem("userImage", newImage);
      setImage(newImage);
      setShowIcon(false);
    }
  };
  const handleIconClick = () => {
    const fileInput = document.getElementById("image-upload");
    fileInput.click();
  };
  const [posts, setPosts] = useState([]);
  const [error, setError] = useState(null);
  const [showCommentForm, setShowCommentForm] = useState(false);
  const handlePostCreated = (post) => {
    setPosts([...posts, post]);
  };
  const handleCommentCreated = (postId, comment) => {
    const updatedPosts = posts.map((post) => {
      if (post.id === postId) {
        return {
          ...post,
          comments: [...post.comments, comment],
        };
      } else {
        return post;
      }
    });
    setPosts(updatedPosts);
  };
  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get("http://127.0.0.1:8000/api/posts/");
        const promises = response.data.map((post) =>
          axios.get(`http://127.0.0.1:8000/api/posts/${post.id}/comments/`)
        );
        const commentResponses = await Promise.all(promises);
        const postsWithComments = response.data.map((post, index) => ({
          ...post,
          comments: commentResponses[index].data,
        }));
        setPosts(postsWithComments);
        console.log(postsWithComments);
      } catch (error) {
        console.error(error);
      }
    };
    fetchData();
  }, []);

  return (
    <>
      {error && <div className="error">{error}</div>}
      <section className="POSTS-BOX">
        <div className="Profile">
          <div className="bg-pro">
            <div className="img-pro">
              {showIcon ? (
                <div className="plus-icon" onClick={handleIconClick}>
                  <i className="fas fa-plus"></i>
                </div>
              ) : (
                <img src={image} />
              )}
              <input
                type="file"
                id="image-upload"
                accept="image/*"
                onChange={handleImageChange}
              />
              <button onclick={handleImageChange}>Edit Photo</button>
            </div>
          </div>

          <div className="Info">
            <div className="text-name">
              <p>John Doe</p>
              <p>Web Developer at Self Employed</p>
            </div>
            <div className="following">
              <span>Following</span>
              <span>34</span>
            </div>
            <div className="followers">
              <span>Follower</span>
              <span>255</span>
            </div>
          </div>
          <button>View Profile</button>
        </div>
        <div className="POSTS">
          <Createposts onPostCreated={handlePostCreated} image={image} />

          {posts.map((post) => (
            <div key={post.id} className="box-pos">
              <ul className="com">
                <li>
                  <button
                    onClick={() => setShowCommentForm(post.id)}
                    className="com"
                  >
                    <i className="fas fa-comment-alt comment"></i> Add Comment
                  </button>
                </li>
              </ul>
              <h2>{post.title}</h2>
              <p>{post.content}</p>
              {post.comments?.map((comment) => (
                <div key={comment.id}>
                  {comment.user && <h5> Commenter: {comment.user?.name}</h5>}
                  <li>{comment.content}</li>
                </div>
              ))}
              {showCommentForm === post.id && (
                <CommentForm
                  postId={post.id}
                  onSubmit={(comment) => handleCommentCreated(post.id, comment)}
                />
              )}
            </div>
          ))}
        </div>
      </section>
    </>
  );
}

export default Community;
