import React, { useState, useEffect } from "react";
import axios from "axios";
import Createposts from "./Createposts";
import img_pro from "../image/icon.png";
import "./Community.css";
import Comment from "./Comment";

function Community() {
  const [posts, setPosts] = useState([]);
  const [error, setError] = useState(null);

  const handlePostCreated = (post) => {
    setPosts([...posts, post]);
  };

  const createComment = async (userId, commentData) => {
    try {
      const url = `http://127.0.0.1:8000/comments/create/${userId}`;
      const response = await axios.post(url, commentData);
      console.log(response.data); // handle the response data
    } catch (error) {
      console.error(error);
    }
  };

  useEffect(() => {
    axios
      .get("http://127.0.0.1:8000/api/posts/")
      .then((response) => {
        setPosts(response.data);
        localStorage.setItem("user_id", response.data.user_id);
      })
      .catch((error) => setError(error.message));
  }, []);

  return (
    <>
      {error && <div className="error">{error}</div>}
      <section className="POSTS-BOX">
        <div className="Profile">
          <div className="bg-pro">
            <div className="img-pro">
              <img src={img_pro} alt="" />
            </div>
          </div>

          <div className="Info">
            <div className="text-name">
              <p>John Doe</p>
              <p>Web Developer at Self Employed</p>
            </div>
            <div className="following">
              <span>Following</span>
              <span>34</span>
            </div>
            <div className="followers">
              <span>Follower</span>
              <span>255</span>
            </div>
          </div>
          <button>View Profile</button>
        </div>
        <div className="POSTS">
          <Createposts onPostCreated={handlePostCreated} />

          {posts.map((post) => (
            <div key={post.id} className="box-pos">
              <h2>{post.title}</h2>
              <p>{post.content}</p>
              <Comment post={post} createComment={createComment} />
            </div>
          ))}
        </div>
      </section>
    </>
  );
}

export default Community;
