import React, { useState, useEffect } from "react";
import axios from "axios";
import Createposts from "./Createposts";

function Community() {
  const [posts, setPosts] = useState([]);
  const [error, setError] = useState(null);

  const handlePostCreated = (post) => {
    setPosts([...posts, post]);
  };

  const handleDeletePost = async (postId) => {
    const url = `http://127.0.0.1:8000/api/posts/${postId}/delete/`;
    const headers = {
      Authorization: `Bearer ${localStorage.getItem("access_token")}`,
    };
    try {
      const response = await axios.get(
        `http://127.0.0.1:8000/api/posts/${postId}/`
      );
      if (response.data.user === parseInt(localStorage.getItem("user_id"))) {
        const deleteResponse = await axios.delete(url, { headers });
        console.log(deleteResponse.data);
        setPosts(posts.filter((post) => post.id !== postId));
      } else {
        console.log("You are not authorized to delete this post");
      }
    } catch (error) {
      console.error(error);
    }
  };
  return (
    <div>
      {error && <div className="error">{error}</div>}
      <Createposts onPostCreated={handlePostCreated} />
      {posts.map((post) => (
        <div key={post.id}>
          <h2>{post.title}</h2>
          <p>{post.content}</p>
          {post.user === localStorage.getItem("user_id") && (
            <button onClick={() => handleDeletePost(post.id)}>Delete</button>
          )}
        </div>
      ))}
    </div>
  );
}

export default Community;
